import React, { useState } from 'react';
import SearchBar from './components/SearchBar';
import Card from './components/Card';

export default function App() {
  const [query, setQuery] = useState('');
  const [cards, setCards] = useState([]);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;

    try {
      const res = await fetch(
        `https://api.scryfall.com/cards/named?exact=${encodeURIComponent(query.trim())}`
      );
      const data = await res.json();
      if (data.object === 'error') {
        alert('Card not found');
        setCards([]);
        return;
      }
      setCards([data]);
    } catch (error) {
      alert('Error fetching card data');
      console.error(error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-800">
      {/* Navigation Bar */}
      <header className="bg-white border-b shadow-sm sticky top-0 z-50">
        <div className="max-w-screen-xl mx-auto px-4 py-3 flex flex-col md:flex-row md:justify-between md:items-center gap-2 md:gap-0">
          <div className="flex items-center gap-3">
            <img
              src="/assets/logo.png"
              alt="CardVerse Logo"
              className="h-10 w-auto object-contain"
            />
            <span className="text-2xl font-bold text-blue-600">CardVerse</span>
          </div>

          {/* Navigation Links */}
          <nav className="flex gap-6 text-sm md:text-base text-gray-700 font-medium">
            <a href="#" className="hover:text-blue-600 transition">Browse</a>
            <a href="#" className="hover:text-blue-600 transition">Decks</a>
            <a href="#" className="hover:text-blue-600 transition">Inventory</a>
            <a href="#" className="hover:text-blue-600 transition">About</a>
          </nav>

          {/* Search Bar (on wider screens aligns right) */}
          <div className="md:ml-auto">
            <SearchBar
              query={query}
              setQuery={setQuery}
              handleSearch={handleSearch}
            />
          </div>
        </div>
      </header>

      {/* Main */}
      <main className="p-6 max-w-6xl mx-auto mt-6">
        {cards.length === 0 ? (
          <p className="text-center text-gray-500">No cards loaded</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {cards.map((card) => (
              <Card key={card.id} card={card} />
            ))}
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-white border-t mt-10 p-4 text-center text-sm text-gray-500">
        &copy; {new Date().getFullYear()} CardVerse. All rights reserved.
      </footer>
    </div>
  );
}
