import React from 'react';

export default function Card({ card }) {
  const oracleText = card.oracle_text || 'No oracle text available';
  const price = card.prices?.usd ? parseFloat(card.prices.usd).toFixed(2) : null;

  return (
    <div className="bg-white rounded-2xl shadow hover:shadow-lg transition duration-300 p-4 flex flex-col items-center text-center">
      {card.image_uris?.small && (
        <img
          src={card.image_uris.small}
          alt={card.name}
          className="mb-3 rounded-md shadow-md"
        />
      )}

      <h2 className="text-lg font-semibold text-gray-800">{card.name}</h2>
      <p className="text-sm text-gray-500">{card.set_name} â€¢ {card.rarity}</p>

      {price && (
        <p className="mt-1 text-green-600 font-semibold">${price}</p>
      )}

      {oracleText && (
        <p
          className="text-xs text-gray-600 mt-2 max-h-[3.5em] overflow-hidden text-ellipsis line-clamp-3 hover:line-clamp-none transition-all duration-200 cursor-pointer"
          title={oracleText}
        >
          {oracleText}
        </p>
      )}
    </div>
  );
}
