import React, { useState } from 'react';
import SearchBar from './components/SearchBar';
import Card from './components/Card';

export default function App() {
  const [query, setQuery] = useState('');
  const [cards, setCards] = useState([]);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;

    try {
      const res = await fetch(
        `https://api.scryfall.com/cards/named?exact=${encodeURIComponent(query.trim())}`
      );
      const data = await res.json();
      if (data.object === 'error') {
        alert('Card not found');
        setCards([]);
        return;
      }
      setCards([data]); // store result as an array to support map()
    } catch (error) {
      alert('Error fetching card data');
      console.error(error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-800">
      {/* Header */}
      <header className="bg-white shadow-md p-4 flex justify-between items-center sticky top-0 z-50">
        <div className="flex items-center gap-2">
          <img src="/assets/logo.png" alt="CardVerse Logo" className="h-10 w-auto" />
          <h1 className="text-2xl font-bold tracking-tight text-blue-600">CardVerse</h1>
        </div>
        <SearchBar
          query={query}
          setQuery={setQuery}
          handleSearch={handleSearch}
        />
      </header>

      {/* Main */}
      <main className="p-4 max-w-6xl mx-auto">
        {cards.length === 0 ? (
          <p className="text-center text-gray-500">No cards loaded</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            {cards.map((card) => (
              <Card key={card.id} card={card} />
            ))}
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-white border-t mt-10 p-4 text-center text-sm text-gray-500">
        &copy; {new Date().getFullYear()} CardVerse. All rights reserved.
      </footer>
    </div>
  );
}
