import React from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import DeckBuilder from './pages/DeckBuilder';
import Inventory from './pages/Inventory';
import About from './pages/About';
import SearchPage from './pages/SearchPage';
import './styles/Background.css';

export default function App() {
  return (
    <div className="min-h-screen flex flex-col text-gray-100 relative overflow-hidden">

      {/* === Background Animated Gradient === */}
      <div
        className="absolute inset-0 bg-animated-gradient bg-size-400 animate-gradient-move"
        style={{ filter: 'blur(80px)', opacity: 0.7, zIndex: -2 }}
      />
      <div className="background-overlay" />

      {/* === Header === */}
      <header className="bg-white/90 backdrop-blur-md border-b shadow-sm sticky top-0 z-50">
        <div className="max-w-screen-xl mx-auto flex items-center justify-between px-4 py-3 md:px-6 md:py-4">

          {/* Logo Section */}
          <div className="flex items-center gap-2">
            <img
              src="/assets/logo.png"
              alt="CardVerse Logo"
              className="h-10 w-10 object-contain"
            />
            <h1 className="text-2xl font-extrabold text-gray-800">
              <span className="text-red-600">Card</span>
              <span className="text-purple-700">Verse</span>
            </h1>
          </div>

          {/* Navigation */}
          <nav className="flex gap-4 md:gap-6 text-base md:text-lg font-medium text-gray-700">
            <Link to="/" className="hover:text-red-600 transition">Browse</Link>
            <Link to="/decks" className="hover:text-red-600 transition">My Decks</Link>
            <Link to="/inventory" className="hover:text-red-600 transition">Inventory</Link>
            <Link to="/about" className="hover:text-red-600 transition">About</Link>
          </nav>
        </div>
      </header>

      {/* === Page Content === */}
      <main className="flex-grow p-4 sm:p-6 max-w-6xl mx-auto mt-6">
        <Routes>
          <Route path="/" element={<SearchPage />} />
          <Route path="/decks" element={<DeckBuilder />} />
          <Route path="/inventory" element={<Inventory />} />
          <Route path="/about" element={<About />} />
        </Routes>
      </main>

      {/* === Footer === */}
      <footer className="bg-white border-t mt-12 p-4 text-center text-sm text-gray-500 shadow-inner">
        &copy; {new Date().getFullYear()} CardVerse. All rights reserved.
      </footer>
    </div>
  );
}
