import React from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import DeckBuilder from './pages/DeckBuilder';
import Inventory from './pages/Inventory';
import About from './pages/About';
import SearchPage from './pages/SearchPage';
import './styles/Background.css';

export default function App() {
  return (
    <div className="min-h-screen flex flex-col text-gray-100 relative overflow-hidden">

      {/* === Background Animated Gradient === */}
      <div
        className="absolute inset-0 bg-animated-gradient bg-size-400 animate-gradient-move"
        style={{ filter: 'blur(80px)', opacity: 0.7, zIndex: -2 }}
      />
      <div className="background-overlay" />

      {/* === Header === */}
      <header className="bg-white/90 backdrop-blur border-b shadow-md sticky top-0 z-50">
        <div className="max-w-screen-xl mx-auto px-6 py-4 flex flex-col md:flex-row md:justify-between md:items-center gap-4 md:gap-0">

          {/* Logo */}
          <div className="flex items-center gap-3">
            <img
              src="/assets/logo.png"
              alt="CardVerse Logo"
              className="h-12 w-auto drop-shadow-md"
            />
            <span className="text-3xl font-extrabold text-blue-600 tracking-wide">
              Card<span className="text-indigo-500">Verse</span>
            </span>
          </div>

          {/* Navigation */}
          <nav className="flex flex-wrap justify-center gap-6 text-gray-700 font-medium text-lg">
            <Link to="/" className="hover:text-blue-600 transition duration-200">
              Browse Cards
            </Link>
            <Link to="/decks" className="hover:text-blue-600 transition duration-200">
              My Decks
            </Link>
            <Link to="/inventory" className="hover:text-blue-600 transition duration-200">
              Inventory
            </Link>
            <Link to="/about" className="hover:text-blue-600 transition duration-200">
              About
            </Link>
          </nav>
        </div>
      </header>

      {/* === Page Content === */}
      <main className="flex-grow p-6 max-w-6xl mx-auto mt-10">
        <Routes>
          <Route path="/" element={<SearchPage />} />
          <Route path="/decks" element={<DeckBuilder />} />
          <Route path="/inventory" element={<Inventory />} />
          <Route path="/about" element={<About />} />
        </Routes>
      </main>

      {/* === Footer === */}
      <footer className="bg-white border-t mt-20 p-4 text-center text-sm text-gray-500 shadow-inner">
        &copy; {new Date().getFullYear()} CardVerse. All rights reserved.
      </footer>
    </div>
  );
}
