import React, { useState } from 'react';
import SearchBar from './components/SearchBar';
import Card from './components/Card';

export default function App() {
  const [query, setQuery] = useState('');
  const [cards, setCards] = useState([]);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;

    try {
      const res = await fetch(
        `https://api.scryfall.com/cards/named?exact=${encodeURIComponent(query.trim())}`
      );
      const data = await res.json();
      if (data.object === 'error') {
        alert('Card not found');
        setCards([]);
        return;
      }
      setCards([data]);
    } catch (error) {
      alert('Error fetching card data');
      console.error(error);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-100 to-gray-200 text-gray-800">
      {/* âœ¨ Fancy Navigation Bar */}
      <header className="bg-white/90 backdrop-blur border-b shadow-md sticky top-0 z-50">
        <div className="max-w-screen-xl mx-auto px-6 py-4 flex flex-col md:flex-row md:justify-between md:items-center gap-4 md:gap-0">
          {/* Logo */}
          <div className="flex items-center gap-3">
            <img
              src="/assets/logo.png"
              alt="CardVerse Logo"
              className="h-12 w-auto drop-shadow-md"
            />
            <span className="text-3xl font-extrabold text-blue-600 tracking-wide">
              Card<span className="text-indigo-500">Verse</span>
            </span>
          </div>

          {/* Nav Links */}
          <nav className="flex flex-wrap justify-center gap-6 text-gray-700 font-medium text-lg">
            <a href="#" className="hover:text-blue-600 transition duration-200">
              Browse Cards
            </a>
            <a href="#" className="hover:text-blue-600 transition duration-200">
              My Decks
            </a>
            <a href="#" className="hover:text-blue-600 transition duration-200">
              Inventory
            </a>
            <a href="#" className="hover:text-blue-600 transition duration-200">
              About
            </a>
          </nav>

          {/* Search Bar */}
          <div className="md:ml-auto">
            <SearchBar
              query={query}
              setQuery={setQuery}
              handleSearch={handleSearch}
            />
          </div>
        </div>
      </header>

      {/* Main Section */}
      <main className="p-6 max-w-6xl mx-auto mt-10">
        {cards.length === 0 ? (
          <p className="text-center text-gray-500">No cards loaded</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {cards.map((card) => (
              <Card key={card.id} card={card} />
            ))}
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-white border-t mt-20 p-4 text-center text-sm text-gray-500 shadow-inner">
        &copy; {new Date().getFullYear()} CardVerse. All rights reserved.
      </footer>
    </div>
  );
}
